// Simple test to trigger the API and see server logs
const testData = {
  audio: "data:audio/pcm;base64,",
  topic: "Test"
};

console.log('Sending test request...');
console.log('Data size:', JSON.stringify(testData).length, 'bytes');

// Use a simple HTTP request
const http = require('http');

const postData = JSON.stringify(testData);

const options = {
  hostname: 'localhost',
  port: 6000,
  path: '/api/transcribe',
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'Content-Length': Buffer.byteLength(postData)
  }
};

const req = http.request(options, (res) => {
  console.log(`Status: ${res.statusCode}`);
  console.log(`Headers:`, res.headers);
  
  let data = '';
  res.on('data', (chunk) => {
    data += chunk;
  });
  
  res.on('end', () => {
    console.log('Response:', data);
  });
});

req.on('error', (e) => {
  console.error(`Request error: ${e.message}`);
});

req.write(postData);
req.end();

console.log('Request sent! Check server terminal for logs...');
